INSERT INTO `d_hero` VALUES ('4', '蓝胖', '4.jpg', '力量：23,智力：17,敏捷：14,最大生命值：587,物理攻击：61,魔法强度：41,物理护甲：7,魔法抗性：2,物理暴击：6', '1', '前排', '智力', '蓝色+2:111|106,紫色:39|37|99|97|64|105,紫色+1:33|92|114|102|122|78,紫色+2:48|123|37|117|78|45,紫色+3:69|127|92|101|117|78', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0', '前排法师，能用来个脑袋连续释放法术；给不给力基本看脸。', '精英第11章双头、精英第12章食人', null, '4');
INSERT INTO `d_hero` VALUES ('5', '大鱼人', '5.jpg', '力量：21,智力：15,敏捷：17,最大生命值：549,物理攻击：55,魔法强度：38,物理护甲：5,魔法抗性：1,物理暴击：7', '2', '前排', '力量', '蓝色+2:97,紫色:51|75|66|91|104|78,紫色+1:71|14|13|17|117|78,紫色+2:65|114|90|110|120|78,紫色+3:94|93|103|118|93|78', '2014-06-12 10:14:00', '2014-06-13 14:02:12', '0', '前排坦克，拥有物理伤害与控制技能。', '精英第3章风暴、精英第4章浅滩登陆、精英第8章冰封堡大门', null, '5');
INSERT INTO `d_hero` VALUES ('6', '电魂', '6.jpg', '力量：21,智力：19,敏捷：22,最大生命值：549,物理攻击：46,魔法强度：48,物理护甲：2,魔法抗性：2,物理暴击：9', '3', '前排', '敏捷', '蓝色+2:103,紫色:32|74|90|97|104|200,紫色+1:71|49|36|100|119|78,紫色+2:34|12|75|110|118|78,紫色+3:71|92|76|117|116|78', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0', '前排坦克，物理输出。电刃链接技能可以偷取敌人的攻击力，打得越久攻击越强。', '精英第2章发电场、精英第4章闪电的味道', null, '6');
