INSERT INTO `d_skill` VALUES ('1', '魂之挽歌', '必点', '释放冤魂，以自己为中心造成大范围的魔法伤害。当影魔死亡时，会立即释放一次魂之挽歌。', '1.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('2', '影压', '必点', '在近，中，远距离连续释放3道影压，造成小范围魔法伤害。（与旧版影魔释放规则相同，分3回合释放）', '2.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('3', '支配死灵', '必点', '每当周围有一个生物死亡，影魔都会吸取他的灵魂来增加的攻击力。支配死灵的效果也会令影压伤害增强。', '3.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('4', '魔王降临', '必点', '影魔的出现让周围生物惊恐不安，大幅降低场上所有敌人的攻击力。', '4.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('5', '冲击', '必点', '进行一次威力巨大的撞击，击飞前面的敌人', '5.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('6', '马蹄践踏', '必点', '猛击地面，对敌方地面单位造成伤害，并使他们晕眩。', '6.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('7', '战争冲锋', '必点', '率先冲进战场，为队友加持斗气盾，吸收伤害。', '7.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('8', '刚毅之躯', '必点', '人马硕大的身躯蕴含着强大的力量。', '8.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('9', '隐匿', '必点', '圣堂刺客能在任何时候进入隐身状态，如果打破隐身状态，能对目标造成格外伤害并降低目标的护甲。', '9.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('10', '折光', '必点', '使用心灵异能在圣堂刺客外形成一层折光外壳，以抵挡一定次数的伤害，并增加下次攻击的伤害。', '10.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('11', '灵能陷阱', '必点', '运用心灵异能制造一个陷阱，触发后降低周围400范围内敌人30%的移动速度。', '11.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('12', '灵能之刃', '必点', '运用他的心灵异能，圣堂刺客一方面增加自己的攻击距离，同时将范围扩展至目标身后的区域。', '12.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('13', '刚毛后背', '必点', '刚背猪露出自己的刚毛后背，大幅减少受到的伤害。', '13.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('14', '粘稠鼻涕', '可选', '用鼻涕包裹一个敌方单位，减少他的护甲。', '14.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('15', '刺针扫射', '必点', '刚背猪累积受到一定伤害（包括刚毛后背减免的部分）后，会自动将身上的尖刺发射出去，对周围的敌人造成物理伤害。', '15.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('16', '战意', '必点', '刚背猪激发自己的战斗意志，增加攻击力。', '16.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('17', '激光炮', '必点', '奋力发射一道耀眼的激光，造成大量魔法伤害', '17.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('18', '兴奋剂', '必点', '牺牲20%的生命，在一定时间内提升机枪兵的攻击力和攻击速度', '18.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('19', '充足活力', '必点', '提高基础暴击几率', '19.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('20', '穿透子弹', '必点', '增加护甲穿透', '20.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('21', '战吼', '神技', '巨魔用狂野的战吼激发队友的战意，提升所有友军25%的移动速度和攻击速度，并提高攻击力和魔法强度。', '21.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('22', '旋风斧', '必点', '巨魔扔出3把旋转的飞斧，对前方大片区域造成物理伤害。', '22.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('23', '战斧技巧', '近战伤害很猛', '巨魔不仅投掷飞斧，还会用巨斧砍杀靠近的敌人。', '23.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('24', '战魂', '必点', '巨魔的攻击异常迅捷，大幅提升攻击速度。', '24.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('25', '地狱火', '必点', '术士召唤陨石从天而降，被砸中的敌人受到魔法伤害和短暂眩晕，陨石落地后将变成威力巨大的地狱火为术士战斗。', '25.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('26', '暗言术', '可选', '术士用真言对范围内最虚弱的人释放持续性魔法，若是友军则治疗，若是敌人则造成伤害。', '26.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('27', '烈火之拳', '必点', '术士召唤的地狱火用烈焰的拳头重击并击晕眼前的敌人。', '27.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('28', '火焰领域', '必点', '术士召唤的地狱火身上的火焰会持续的灼烧塔附近的敌人', '28.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('29', '恶魔变身', '必点', '当灵魂守卫拥有一半以上能量时，他就可以变身为一直强大的恶魔。恶魔形态持续消耗能量并具有远程攻击能力。', '29.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('30', '镜像', '必点', '制造一个灵魂守卫的幻象来帮助战斗。', '30.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('31', '灵魂汲取', '必点', '灵魂守卫锁定一个敌方单位。汲取生命并转化给自己。', '31.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('32', '恶魔之血', '必点', '恶魔的血液会强化灵魂守卫的身体，增加生命。', '32.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('33', '能量虚空', '必点', '敌法闪烁到智力最高的敌人身后，以目标为中心施放能量虚空，造成巨大的魔法伤害。', '33.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('34', '能量燃烧', '虐法师队必点', '敌法闪烁到智力最高的敌人身边攻击，造成小范围物理伤害并额外损毁敌人的能量。', '34.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('35', '战刃技巧', '可选', '敌法加速旋转他的刀刃，增加他的敏捷。', '35.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('36', '魔法盾', '可选', '敌法利用对魔法能量的理解减少受到的伤害，增加魔抗。', '36.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('37', '亡灵召唤', '打断输出二合一', '召唤大群怨灵攻击敌人，对全场敌人造成魔法伤害，并为死亡先知吸取一定比例的生命值。吸取的生命值会在怨灵消散后输送给死亡先知。', '37.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('38', '蝠群冲击', '增加输出', '死亡先知用巫术召唤一大群蝙蝠，攻击前方大面积敌人造成魔法伤害。', '38.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('39', '沉默', '控制对方', '死亡先知利用死亡魔法沉默大范围的敌人，使其无法释放法术。', '39.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('40', '巫术精修', '增加输出', '死亡先知不断的提升自己的巫术技巧，增加魔法强度。', '40.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('41', '醉拳', '必点', '熊喵的拳脚功夫把敌人打的七晕八素，造成一连串的物理伤害。', '41.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('42', '酒雾', '必点', '击碎一个酒桶，用弥散的酒精笼罩敌人，造成魔法伤害。醉酒的敌人会大幅降低物理命中和闪避率。', '42.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('43', '雷霆震落', '可选', '熊喵用他沉重的身体跺击大地，造成物理伤害，并短暂眩晕敌人。', '43.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('44', '元素之力', '别点', '元素的力量让熊喵的身体更加强壮，增加力量。', '44.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('45', '剧毒撕咬', '必点', '亚龙吐出一团毒液，并造成巨大的持续伤害。', '45.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('46', '毒液之球', '必点', '亚龙射出一小团毒液，对敌人造成魔法伤害。毒液将腐蚀敌人的护甲，并在一段时间内造成持续伤害。', '46.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('47', '毒液皮肤', '可选', '亚龙的身体由毒素覆盖，可以抵御魔法的伤害。', '47.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('48', '淬毒箭矢', '必点', '亚龙在他的箭矢上淬毒，大幅提升物理攻击力。', '48.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('49', '战术核打击', '必点', '使用核弹打击敌军后方的重要目标，造成范围物理伤害。', '49.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('50', '地狱火飞弹', '必点', '发射一枚地狱火飞弹，穿过前排敌人打击敌军后方。对击中的敌人造成物理伤害并眩晕。', '50.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('51', '加特林机枪', '必点', '飞机掠过敌方阵地，并用机枪扫射所有敌人。', '51.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('52', '穿甲弹头', '必点', '直升机使用最新的穿甲弹头，增加护甲穿透。', '52.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('53', '星落', '可选', '神箭召唤流星雨，对自己周围的敌人造成魔法伤害。', '53.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('54', '虎跃', '物理队核心技能', '英勇地跳进战场并鼓舞友军。提升所有友军25%的移动速度和攻击速度，并提高攻击力和魔法强度。', '54.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('55', '月神箭', '必点', '射出月神之箭，对命中的敌人造成魔法伤害和长时间的眩晕。', '55.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('56', '伏击', '必点', '让友军隐藏在月色的阴影中，增加全队的敏捷。', '56.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('57', '牺牲', '逗逼技能！', '和一个敌人交换位置。如果复仇已经学会魔法箭技能，还会立即对此敌人释放一次魔法箭。', '57.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('58', '魔法箭', '小控制技能', '向一个敌人发射魔法箭，造成魔法伤害，并晕眩。', '58.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('59', '波动', '减甲神技！', '唤起敌人内心深处的恐惧，对一大片区域内的敌人造成魔法伤害，并降低他们的护甲。', '59.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('60', '命令光环', '核心技能', '以复仇的名义而战，增加全队的物理攻击力。', '60.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('61', '死亡契约', '点满秒全队残血', '签订契约，回复自身血量，并大幅度增加攻击力，效果持续到本场战斗结束（可叠加）。', '61.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('62', '火箭', '可选', '射出一只火箭，造成额外物理伤害。', '62.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('63', '速射', '必点', '速射，短暂的增加攻击速度。', '63.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('64', '隐匿刺杀', '必点', '隐匿在暗处现身一击，增加暴击', '64.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('65', '飓风箭', '打断输出', '射出风之箭矢，击退途经的敌人造成魔法伤害。', '65.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('66', '束缚箭', '小控', '对敌方造成魔法伤害并眩晕。', '66.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('67', '集火术', '可选', '风行集中火力对目标快速射出数只箭矢。', '67.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('68', '风行术', '可选', '风行疾行如风，躲闪敌人的攻击，增加闪避。', '68.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('69', '狙击', '必点', '狙击敌方最虚弱的目标，造成巨大的物理伤害。', '69.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
