INSERT INTO `d_skill` VALUES ('149', '回光', '远征神器', '死亡骑士为自己创造一个吸收伤害的护盾，护盾吸收的伤害将转化为死亡骑士的生命值。', '149.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('150', '缠绕', '增加治疗量', '向战场上最虚弱的单位发射死亡缠绕。如果他是敌人，死亡缠绕会对他造成魔法伤害，如果他是友军，死亡缠绕会治疗他的生命值。', '150.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('151', '暗光盾', '输出加防御', '用黑暗能量创造一面暗之盾，保护最虚弱的队友，当暗之盾消失时，黑暗能量会发生爆炸，对附近的敌人造成魔法伤害。', '151.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('152', '冰霜结界', '有钱必点', '死亡骑士身边凝结了强大的冰霜结界，可以保护他的队友，抵御魔法伤害。', '152.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('153', '投掷', '突进秒后排', '抓住一个敌人并扔出去，对所有被砸中的敌人造成魔法伤害。', '153.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('154', '拔树', '可选', '小小拔起一棵树作为武器，大幅增加攻击范围，并提升伤害。', '154.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('155', '山崩', '打断技能', '用岩石轰炸一个区域，造成魔法伤害，并晕眩敌人。', '155.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('156', '石肤', '必点', '小小用花岗岩强化自己的身体，大幅提升护甲。', '156.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('157', '淘汰之斧', '太猛，不解释了', '巨斧从不怜悯弱者。如果敌人的生命值低于30%，则造成巨大的物理伤害，否则只造成少量伤害。如果淘汰之斧杀死了敌人，斧王将获得额外能量奖励。', '157.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('158', '战争饥渴', '别点', '点燃一个 敌人内心的愤怒，使他在一定时间内持续流失生命值。', '158.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('159', '反击螺旋', '伤害还可以', '斧王反击他的敌人，对周围所有敌人造成物理伤害。', '159.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('160', '咆哮', '必点', '战争怒吼大幅提升斧王的护甲。', '160.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('161', '无敌斩', '杀人如切菜技', '瞬间出剑6次，攻击敌阵中随机目标。因为速度太快，没有人在施放无敌斩期间击中剑圣。', '161.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('162', '剑刃风暴', '无法被打断', '舞起剑刃风暴切人敌阵，对周围的敌方单位造成物理伤害。剑圣此时对魔法免疫。', '162.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('163', '治疗图腾', '谁说我不能奶', '放置一个治疗图腾，为所有队友缓慢回复生命值。', '163.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('164', '剑舞', '加攻击', '高超的剑术可以轻易击中敌人的要害，大幅提高敏捷。', '164.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('165', '死神召唤', '必点', '召唤死神斩击最弱的敌人，根据敌人已经损失的生命值，给予一定比例的魔法伤害，并眩晕目标。', '165.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('166', '生命脉动', '必点', '释放一批亡魂，用它们的能量治疗附近的队友，并伤害附近的敌人。', '166.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('167', '生命契约', '能抗才是王道', '死灵法师凭借对生命的深刻理解，大幅增加生命值。', '167.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('168', '魔法契约', '必点', '死灵法师凭借对死亡的深刻理解，大幅增加魔法抗性。', '168.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('169', '海妖之歌', '有一姐队必点', '塞壬海妖的歌声让附近的敌人陷入沉睡。', '169.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('170', '激流斩击', '别点', '挥舞长刀，对附近的敌人造成物理伤害。', '170.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('171', '诱捕', '有一姐队必点', '娜迦撒出一张网，造成魔法伤害。被网住的敌人无法移动、闪避或进行物理攻击。', '171.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('172', '镜像防御', '别点', '娜迦的剑术出神入化，可以轻易闪避敌人的攻击。', '172.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('173', '狂击', '高伤高吸血', '拍拍熊陷入狂怒，如暴风骤雨一般打击前方的敌人，造成多次物理伤害。', '173.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('174', '震击', '别点', '用熊抓拍击大地，对附近的敌人造成魔法伤害并造成减速效果。', '174.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('175', '宣泄', '提升1技能效果', '野性的怒火使拍拍熊大幅提升暴击。', '175.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('176', '激怒', '可选', '野兽的力量使拍拍熊大幅提升攻击力。', '176.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('177', '飞锤', '打断', '丢出一个魔法锤，眩晕目标。', '177.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('178', '暴击', '必点', '用力劈砍目标，造成较高物理伤害。', '178.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('179', '吸血光环', '提升输出生存能力', '增加己方全体的吸血效果。', '179.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('180', '不死', '可能发生奇迹', '骷髅王在死亡后3秒会自动复活，并恢复一定生命值。骷髅王每次出战只能复活1次。', '180.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('181', '沟壑', '必点', '用图腾猛砸地表，形成巨大的沟壑。对所有敌人造成魔法伤害，并震晕他们。', '181.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('182', '强化图腾', '必点', '横扫面前的敌人，造成魔法伤害。并击晕他们。', '182.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('183', '回音', '必点', '重击地面，对附近的敌人造成魔法伤害,，并击晕他们。', '183.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('184', '力量强化', '别点', '牛头人强化自己的体格，大幅提升力量。', '184.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('185', '等离子场', '必点', '自身放出一圈电球，对大范围敌人照成魔法伤害。', '185.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('186', '风暴', '必点', '召唤乌云和闪电，对目标造成多次魔法伤害。', '186.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('187', '电刃链接', '必点', '偷取一名敌人的物理攻击力，效果持续至本场战斗结束。', '187.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('188', '紊乱电流', '必点', '电魂身体周围的电流可以中和魔法，增加魔抗。', '188.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('189', '碎击', '打断技能必点', '锤击地面，对附近的敌人造成魔法伤害并眩晕。', '189.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('190', '重击', '别点', '挥舞三叉戟，对附近的敌人造成物理伤害。', '190.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('191', '伤害加深', '增加输出', '标记一名敌人，一段时间内削弱他的护甲。', '191.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('192', '力量强化', '别点', '属性强化，增加自己的力量。', '192.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('193', '爆轰', '看脸', '原始的食人魔法术，对敌人造成魔法伤害，可以触发多重施法。', '193.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('194', '燃烧瓶', '可选', '投掷一个燃烧瓶将敌人点燃，造成魔法伤害。燃烧的火焰会对附近的所有敌人造成持续伤害，有几率多重施法。', '194.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('195', '嗜血术', '可选', '随机对一名队友释放嗜血术，大幅提高他的攻击和施法速度，有几率多重施法。', '195.jpg', '2014-06-12 10:14:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('196', '力量强化', '可选', '食人魔体格强壮，大幅提升力量。', '196.jpg', '2014-06-12 10:14:00', '2014-06-24 15:07:26', '0');
