INSERT INTO `d_skill` VALUES ('198', '海浪', '增加输出', '召唤一道海浪打击敌人，一段时间内削弱他的护甲。', '198.jpg', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('199', '锚击', '别点', '轮动铁锚，对自身附近的敌人造成物理伤害。', '199.jpg', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('200', '硬壳', 'T重要的是能抗', '潮汐坚硬的外壳可以抵挡伤害，增加自身的护甲。', '200.jpg', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('201', '守护', '对物理队全满', '召唤神术守护队友，让所有队友抵抗物理伤害。', '201.jpg', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('202', '圣光', '增加治疗量', '用圣光治疗一个最虚弱的队友。', '202.jpg', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('203', '驱散', '对魔法队全满', '用神术驱散伤害魔法，为一名队友抵抗魔法伤害。', '203.jpg', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('204', '祝福', '必点无争议', '为队友施加祝福，减少他们受到的物理伤害。', '204.jpg', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0');
INSERT INTO `d_skill` VALUES ('205', '幽灵船', '过渡用', '召唤幽灵船冲向对方，对敌造成大范围的眩晕和魔法伤害。', '205.jpg', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0');
